 Values
   (9223, 'Fresatore', 'Effettua la fresatura di pezzi in metallo o plastica sulla fresatrice o macchina a controllo numerico', 'Licenza di scuola media, corso professionale e apprendistato.', '7.2.1.1.21', TO_DATE('01/01/2006 00:00:00', 'MM/DD/YYYY HH24:MI:SS'), TO_DATE('01/01/2006 12:00:00', 'MM/DD/YYYY HH24:MI:SS'), '7.2.1.1', '7.2.1');
