 Values
   (42, 'Operatore macchine tessili', 'Predisporre le macchine tessili e curare l''esecuzione del lavoro.', 'Diploma di istituto professionale, corsi previsti dal settore, apprendistato.', '7.2.6.9.23', TO_DATE('01/01/2006 00:00:00', 'MM/DD/YYYY HH24:MI:SS'), TO_DATE('01/01/2006 12:00:00', 'MM/DD/YYYY HH24:MI:SS'), '7.2.6.9', '7.2.6');
