   (COD_PROFILO, DES_PROFILO, TXT_FINALITA, TXT_PREREQUISITI, COD_FIGURAPROFESS, DAT_INIZIO, DTT_TMST, COD_CATEGORIAPROFESS, COD_AREAPROFESS)
 Values
   (120003, 'Addetto alla rendicontazione di progetti formativi', 'L''addetto alla rendicontazione di progetti formativi risponde al coordinatore dell''area di riferimento e gestisce le procedure contabili e amministrative di un progetto. Conosce le modalit¨i rendicontazione dei fondi strutturali e gestisce il rapporto con gli interlocutori di progetto.', 'Formazione preferibilmente di livello universitario', '3.3.1.3.1', TO_DATE('01/01/2006 00:00:00', 'MM/DD/YYYY HH24:MI:SS'), TO_DATE('01/01/2006 12:00:00', 'MM/DD/YYYY HH24:MI:SS'), '3.3.1.3', '3.3.1');
