   (COD_PROFILO, DES_PROFILO, TXT_FINALITA, TXT_PREREQUISITI, COD_FIGURAPROFESS, DAT_INIZIO, DTT_TMST, COD_CATEGORIAPROFESS, COD_AREAPROFESS)
 Values
   (53, 'Gestore del punto vendita', 'Gestisce consegna, inventario, disposizione e vendita dei prodotti. Deve individuare gli strumenti pi¨ efficaci per pubblicizzare le vendite, verificare l''andamente budgetario, redigere bolle di consegna e fatture, pianificare le vendite e saper condurre trattative commerciali. . In alcuni casi si occupa anche della gestione del personale e delle comunicazioni con i responsabili dei diversi settori aziendali. Si occupa dell¨organizzazione di attivit¨irate ad aumentare il livello di motivazione del personale aziendale.', 'Non esistono requisiti, ma si preferisce una preparazione di scuola superiore e la frequenza di corsi di qualifica organizzati dalle associazioni di categoria.', '5.1.2.2.14', TO_DATE('01/01/2006 00:00:00', 'MM/DD/YYYY HH24:MI:SS'), TO_DATE('01/01/2006 12:00:00', 'MM/DD/YYYY HH24:MI:SS'), '5.1.2.2', '5.1.2');
Insert into DEC_PROFILI
   (COD_PROFILO, DES_PROFILO, TXT_FINALITA, TXT_PREREQUISITI, COD_FIGURAPROFESS, DAT_INIZIO, DTT_TMST, COD_CATEGORIAPROFESS, COD_AREAPROFESS)
 Values
   (60, 'Carpentiere in legno', 'Si occupa della progettazione, fabbricazione e installazione delle strutture in legno necessarie per la cantieristica: casseformi per il calcestruzzo, scale semplici, giunti, passerelle, ossature in legno per nuovi edifici.Cura la fabbricazione e l''installazione nei cantieri delle strutture in legno."', 'Apprendistato, esperienza nel campo della carpenteria.', '6.1.2.3.6', TO_DATE('01/01/2006 00:00:00', 'MM/DD/YYYY HH24:MI:SS'), TO_DATE('01/01/2006 12:00:00', 'MM/DD/YYYY HH24:MI:SS'), '6.1.2.3', '6.1.2');
Insert into DEC_PROFILI
   (COD_PROFILO, DES_PROFILO, TXT_FINALITA, TXT_PREREQUISITI, COD_FIGURAPROFESS, DAT_INIZIO, DTT_TMST, COD_CATEGORIAPROFESS, COD_AREAPROFESS)
 Values
   (65, 'Addetto alla accoglienza ed alla reception alberghiera', 'Curare l`accoglienza dei clienti nel corso del soggiorno, dall`arrivo alla partenza ed informarli sui servizi interni ed esterni. Effettuare la pianificazione delle prenotazioni e dell`occupazione delle camere, la contabilit¨iornaliera, il rapporto con i fornitori. L`attivit¨i svolge a contatto con la clientela, gli orari sono sfalsati, le domeniche e i giorni festivi sono lavorativi, il lavoro ¨rganizzato in turni. I contratti stagionali sono frequenti in certe zone.', 'Diploma di scuola secondaria (Istituto turistico).', '4.2.2.1.2', TO_DATE('01/01/2006 00:00:00', 'MM/DD/YYYY HH24:MI:SS'), TO_DATE('01/01/2006 12:00:00', 'MM/DD/YYYY HH24:MI:SS'), '4.2.2.1', '4.2.2');
Insert into DEC_PROFILI
   (COD_PROFILO, DES_PROFILO, TXT_FINALITA, TXT_PREREQUISITI, COD_FIGURAPROFESS, DAT_INIZIO, DTT_TMST, COD_CATEGORIAPROFESS, COD_AREAPROFESS)
 Values
   (67, 'Animatore turistico', 'Coordinare e curare tutte le attivit¨i animazione (ricreative, sportive e culturali) per la gestione del tempo libero di gruppi di turisti al fine di rendere il loro soggiorno maggiormente piacevole. CONTESTI LAVORATIVI POSSIBILI: Solitamente questa figura opera nei villaggi turistici, nei viaggi organizzati, presso grandi alberghi o campeggi di notevoli dimensioni, ecc."', 'Diploma scuola superiore (istituto per il turismo).', '3.4.1.3.7', TO_DATE('01/01/2006 00:00:00', 'MM/DD/YYYY HH24:MI:SS'), TO_DATE('01/01/2006 12:00:00', 'MM/DD/YYYY HH24:MI:SS'), '3.4.1.3', '3.4.1');
Insert into DEC_PROFILI
   (COD_PROFILO, DES_PROFILO, TXT_FINALITA, TXT_PREREQUISITI, COD_FIGURAPROFESS, DAT_INIZIO, DTT_TMST, COD_CATEGORIAPROFESS, COD_AREAPROFESS)
 Values
   (68, 'Responsabile di sala', 'Assicurare il buon funzionamento del servizio di ristorazione, dirigere, coordinare e controllare il lavoro del personale, coordinando i servizi ed i compiti connessi alla gestione del ristorante: pulizia, igiene e sicurezza, accoglienza e servizio del cliente, gestione delle scorte e approvvigionamenti, incassi.L`impiego si svolge principalmente nelle aziende di ristorazione private. Gli orari di lavoro sono sfasati. La domeniche ed i giorni festivi possono essere lavorativi.', 'Diploma di scuola media, corsi di formazione professionale. Utili: diploma di scuola superiore (Istituto alberghiero, Istituto per il turismo), esperienze maturate sul campo.', '5.2.2.3.16', TO_DATE('01/01/2006 00:00:00', 'MM/DD/YYYY HH24:MI:SS'), TO_DATE('01/01/2006 12:00:00', 'MM/DD/YYYY HH24:MI:SS'), '5.2.2.3', '5.2.2');
Insert into DEC_PROFILI
   (COD_PROFILO, DES_PROFILO, TXT_FINALITA, TXT_PREREQUISITI, COD_FIGURAPROFESS, DAT_INIZIO, DTT_TMST, COD_CATEGORIAPROFESS, COD_AREAPROFESS)
 Values
   (74, 'Tecnico di servizi di manutenzione', 'Si occupa dei lavori necessari alla manutenzione ordinaria e straordinaria delle macchine destinate alla produzione. E'' richiesta anche la capacit¨i prevenire i guasti e di progettare modifiche agli impianti.', 'Diploma di scuola dell''obbligo e corsi professionali. Preferibile: diploma di scuola superiore a indirizzo tecnico, esperienza.', '6.2.3.1.14', TO_DATE('01/01/2006 00:00:00', 'MM/DD/YYYY HH24:MI:SS'), TO_DATE('01/01/2006 12:00:00', 'MM/DD/YYYY HH24:MI:SS'), '6.2.3.1', '6.2.3');
